    \begin{tikzpicture}[node distance=1.5cm, auto]
    % Define styles
    \tikzstyle{startstop} = [rectangle, rounded corners, minimum width=3cm, minimum height=1cm,text centered, draw=black, fill=red!30]
    \tikzstyle{process} = [rectangle, minimum width=3cm, minimum height=1cm, text centered, draw=black, fill=orange!30]
    \tikzstyle{decision} = [diamond, aspect=2, text centered, draw=black, fill=green!30]
    \tikzstyle{block} = [rectangle, minimum width=3cm, minimum height=1cm, text centered, draw=black, fill=gray!30]

    % Nodes
    \node (start) [startstop] {Input data};
    \node (init) [startstop, below of=start] {Initialization};
    \node (computation) [block, below of=init, yshift=-0.5cm] {Computation};
    \node (grid) [process, below of=computation] {Grid variation};
    \node (feasibleOp) [process, below of=grid] {Generation of a Feasible Operation Region};
    \node (feasiblePlan) [process, below of=feasibleOp] {Generation of a Feasible Planning Region};
    \node (check) [decision, below of=feasiblePlan, yshift=-0.5cm] {Is grid refinement needed?};
    \node (output) [startstop, right of=check, xshift=3cm] {Output};

    % Arrows
    \draw [arrow] (start) -- (init);
    \draw [arrow] (init) -- (computation);
    \draw [arrow] (computation) -- (grid);
    \draw [arrow] (grid) -- (feasibleOp);
    \draw [arrow] (feasibleOp) -- (feasiblePlan);
    \draw [arrow] (feasiblePlan) -- (check);
    \draw [arrow] (check) -- node[anchor=south] {yes} (grid);
    \draw [arrow] (check.east) -- node[anchor=south] {no} (output);
\end{tikzpicture}
    \end{document}

